import React, { useEffect } from "react";
import Axios from 'axios';
import {useNavigate} from "react-router-dom";

function AddDistribution(){
    const navigate = useNavigate();
    const [partners, setPartners] = React.useState([])
    const [items, setItems] = React.useState([])
    const [locations, setLocations] = React.useState([])
    const [formData, setFormData] = React.useState(
        {
          Partner: 0,
          RequestDate: "",
          CompleteDate: "",
          item:0,
          location:0,
          Quantity: 0,
          Value: 0.00,
          DeliveryMethod: "",
          Comments: "",
          status: ""
        }
      )

        const addItem = 
        <div style={{display: "flex"}}>
          <select>
            <option value="">--Please choose an option--</option>
            {items.map((val) =>{
                    return(
                      <option value={val.Item_id}>{val.Name}</option>
                    )
            })}
          </select>

          <select>
            <option value="">--Please choose an option--</option>
            {locations.map((val) =>{
                    return(
                      <option value={val.Location_id}>{val.Name}</option>
                    )
                  })}
          </select>
          <input type="number" name="Quantity" id="Quantity" required onChange={handleChange} value={formData.Quantity}/>
        </div>
        
        function handleChange(event){
            setFormData(prevFormData => {
              return{
                ...prevFormData,
                [event.target.name]: event.target.value
              }
            })
          }

        function handleSubmit(e){
            e.preventDefault();
            
            Axios.post("http://localhost:3001/distribution/new", {partner:formData.Partner, 
            date:formData.date, 
            source:formData.source, 
            totalitems: formData.totalItems, 
            value: formData.value, 
            deliverymethod: formData.deliveryMethod, 
            comments: formData.comments, 
            state: formData.state},{
              headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
              }
          });
            window.location.href = "/distribution";
      
          }

          useEffect(() => {
            Axios.get("http:////localhost:3001/partner").then((response) =>{
                    setPartners(response.data);
                })
          }, [])
          
          useEffect(() => {
            Axios.get("http:////localhost:3001/item").then((response) =>{
                    setItems(response.data);
                })
          }, [])

          useEffect(() => {
            Axios.get("http:////localhost:3001/location").then((response) =>{
                    setLocations(response.data);
                })
          }, [])

          

          return(
            <form id="distribution" onSubmit={handleSubmit}>
                <label htmlFor="Partner">Partner</label>
                <select id="Partner" name="Partner" value={formData.Partner} onChange={handleChange}>
                  <option value="">--Please choose an option--</option>
                  {partners.map((val) =>{
                    return(
                      <option value={val.Partner_id}>{val.Name}</option>
                    )
                  })}
          
                </select><br/>
                <label htmlFor="RequestDate">RequestDate</label>
                <input type="RequestDate" name="RequestDate" id="RequestDate" value={formData.RequestDate} min="2023-09-01" required onChange={handleChange}/>
                
                <label htmlFor="CompleteDate">CompleteDate</label>
                <input type="CompleteDate" name="CompleteDate" id="CompleteDate" value={formData.CompleteDate} min="2023-09-01" required onChange={handleChange}/>

                <p>Delivery Method</p>

                <label htmlFor="Drop-off">Drop Off</label>
                <input type="radio" id="Drop-off" name="DeliveryMethod" value="Drop-off" checked={formData.DeliveryMethod === "Drop-off"} onChange={handleChange}/>
                <label htmlFor="Other">Other</label>
                <input type="radio" id="Other" name="DeliveryMethod" value="Other" checked={formData.DeliveryMethod === "Other"} onChange={handleChange}/>

                <textarea name="Comments" rows="4" cols="50" onChange={handleChange} placeholder="Comments"></textarea>

                <div>
                  <h2>Items</h2>
                  {addItem}
                </div>
                

                <input type="submit" value="Submit"/>
            </form>
          )
}

export default AddDistribution;
